import { useState,useEffect } from 'react'
import { Link, useNavigate,useParams } from 'react-router-dom'
import { toast } from 'react-toastify'
// import { createAPI } from '../Service/Product'
import { updateAPI } from '../Service/Product'
import { getByIdAPI } from '../Service/Product'

 function UpdateUser() {

    const [product,setProduct] = useState([])
    const [name,setName] = useState({
      name : ''
    })
    const [price,setPrice] = useState({
      pri :''
    })


    const navigate = useNavigate()

    const check = async()=>{

        if(name.length == 0){
            toast.warning('Enter Product Name')
        }else if(price.length==0){
            toast.warning('Enter Price ')
        }else{
            console.log(name,price)
          //  const op = await createAPI(proName,proPrice)

          //  if(op[`status`]=='success'){

          //   toast.success(" Element Added ...")
          //   navigate('/getAll')
          //  }else{
          //   toast.error(op[`error`])
          //  }

        }
    } 

    const{id} = useParams()


    useEffect(() => {
      const fetchData = async() => {

        const op = await getByIdAPI(id)
        const result = op.data[0]
       console.log(op.data)
      //  console.log()
        setProduct(result)

        // console.log(result)


      };

      fetchData();
  }, []);


  const onUpdate=()=>{

    check()

  }


    const onCancel=()=>{
        navigate('/getAll')
    }
    return (




        <div className="mt-5">

            <div className="heading"><h2>Update Page</h2></div>
            <div className="row">
                <div className="col"></div>
                <div className="col">
                    <div className="form form-div">
                        <div className='mt-5'>
                            <label>Product Name : </label>
                            <input onChange={(e)=>{
                                setName({...price , name:e.target.value})

                            }}
                           value={product.proName} type="email" className="form-control " />
                        </div>
                        <div className="mt-5">
                            <label>Product Price </label>
                            <input onChange={(e)=>{
                                setPrice({...price , pri:e.target.value})

                            }}

                            value={product.proPrice}  type="number" className="form-control" />
                        </div>
                        <div className='mb-3'>


                            <center>
                                <button onClick={onUpdate} className='mt-4 btn btn-success'>
                                 UPDATE
                                </button>

                                {/* <Link to='/register'> Register here </Link> */}

                                <button onClick={onCancel} className='mt-4 ms-4 btn btn-primary'>
                                    CANCEL
                                </button>
                            </center>

                        </div>
                    </div>

                </div>
                <div className="col"></div>
            </div>
        </div>
    )
  }

export default UpdateUser
